/**
 * This will build a sqlUrl variable containing all SQL WHERE clause based on a JSON based urlArray
 * as this code will be called before running the SQL request, the sqlUrl will be computed at the
 * right time.
 */

function onTransactionStarted() {
	var urls = eval('(' + urlArray +')');
	//log.error("#############" + urls);
	sqlUrl = "";	
	for (i=0; (i < urls.length-1); i++ ) {
		sqlUrl = sqlUrl + "('" + site + "', '" + urls[i] + "'),\r";
	}
	sqlUrl = sqlUrl + "('" + site + "', '" + urls[i] + "')";
	//log.error("#############" + sqlUrl);
}

