// Handles the transaction start event.
function onTransactionStarted() {
	var url = context.connector.sUrl;
	var newUrl = database + "/_design/" + designdoc + "/_view/" + viewname;
	if (url.indexOf('?') == -1) 
		context.connector.sUrl += newUrl;
	else {
		urlEnd = 	url.substring(url.indexOf('?'));
		urlBase =   url.substring(0, url.indexOf('?'))
		context.connector.sUrl = urlBase + newUrl + urlEnd;
	} 
}

