transaction.handlers:
// Handles the transaction start event.
function onTransactionStarted() {
	provider = context.get("oAuthProvider");
	consumer = context.get("oAuthConsumer");
	provider.retrieveAccessToken(consumer, VerificationCode);
	var token 		= consumer.getToken();
	var tokenSecret = consumer.getTokenSecret(); 
	context.addTextNodeUnderRoot("token", token)
	context.addTextNodeUnderRoot("tokenSecret", tokenSecret)
    return "cancel";
}

// Handles the transaction start event.
function onTransactionStarted() {

	var consumer = Packages.oauth.signpost.basic.DefaultOAuthConsumer(
                ConsumerKey,
                ConsumerSecret);


    var provider = new Packages.oauth.signpost.basic.DefaultOAuthProvider(
                requestTokenUrl,
                "https://twitter.com/oauth/access_token",
                "https://twitter.com/oauth/authorize");

    var url = provider.retrieveRequestToken(consumer, "oob");
    context.addTextNodeUnderRoot("OobUrl", url)
    context.set("oAuthProvider", provider);
    context.set("oAuthConsumer", consumer);
    return "cancel";
}

// Handles the transaction start event.
function onTransactionStarted() {

	var consumer = Packages.oauth.signpost.basic.DefaultOAuthConsumer(
                ConsumerKey,
                ConsumerSecret);

	consumer.setTokenWithSecret(AccessToken, AccessTokenSecret);
    context.set("oAuthConsumer", consumer);
    return "cancel";
}

// Handles the transaction start event.
function onTransactionStarted() {
	context.connector.sUrl = "https://api.twitter.com/1.1/statuses/user_timeline.json";

}

// Handles the transaction start event.
function onTransactionStarted() {
	var btc = ConsumerKey + ":" + ConsumerSecret;
	var b64btc = Packages.com.twinsoft.convertigo.engine.util.Base64.encodeString(btc, false);
	var header = new Packages.com.twinsoft.convertigo.beans.common.XMLVector();
	header.add("Authorization");
	header.add("Basic " + b64btc);
	context.connector.httpParameters.add(header);
	context.connector.sUrl = ApiUrl + "/oauth2/token";
	context.connector.postQuery = "grant_type=client_credentials";
}
