transaction.handlers:
// Handles the transaction start event.
function onTransactionStarted() {
	// Build a OAuth service
	OAuthServiceBuilder = new Packages.org.scribe.builder.ServiceBuilder();
	OAuthServiceBuilder.provider(Packages.org.scribe.builder.api.YammerApi);
	OAuthServiceBuilder.apiKey(apiKey);
	OAuthServiceBuilder.apiSecret(apiSecret);
	OAuthService = OAuthServiceBuilder.build();
	
	// request a Token
	requestToken = OAuthService.getRequestToken();
	token = requestToken.getToken();
	
	// Save Service, token and helper functions in the context
	context.set("OAuthService",OAuthService);
	context.set("requestToken",requestToken);
	
	// save shared helper functions in the context
	context.set("invokeApiFunction",invokeApi);
	context.set("parseResponseFunction",parseResponse);
	
	// prevent C8O to perform actions as we did all the work here.
	return "cancel";
}

// Handles the XML generated event.
function onXmlGenerated() {
	// return in the C8O response the token we got from OAuth..
	context.addTextNodeUnderRoot("Token", token);
}


/**
 * This shared javascript function invoked the OAuth request. It will be called
 * from all the api Transactions.
 */
function invokeApi(context, log, uri, params) {
	// retriev from context the OAuth data
	OAuthService 	= context.get("OAuthService");
	accessToken		= context.get("accessToken");

	// Build request
	if (params.length > 0) {
		uri += "?" + params;
	}

	log.debug(uri);
	
	// Build OAuth request
	OAuthRequest    = new Packages.org.scribe.model.OAuthRequest(
							Packages.org.scribe.model.Verb.GET,
							uri
					  );
					  
	// Sign it and invoke the API				  
	OAuthService.signRequest(accessToken, OAuthRequest);
	response = OAuthRequest.send();
	return response;
}

/**
 * This shared javascript function parses the OAuth response and inserts
 * it in the Convertigo response. It will be called on each onXmlGenerated 
 * transaction handlers.
 */
function parseResponse(dom, response) {
	// Parse the OAuth response
	RespDocument = Packages.com.twinsoft.convertigo.engine.util.XMLUtils.parseDOM("java", response.getBody());
	
	// Insert it in C8O response
	node = dom.importNode(RespDocument.getDocumentElement(), true);
	dom.getElementsByTagName("document").item(0).appendChild(node);
}

// Handles the transaction start event.
function onTransactionStarted() {
	uri    = "https://www.yammer.com/api/v1/search";
	params = "";
	if (search)
		params += "search=" + search + "&";
		
/*	if (count)
