		params += "keywords=" +
				  Packages.java.net.URLEncoder.encode(keywords) + "&";
				  
	if (fieldset)
		uri += ":" + fieldset;*/
	
	response = context.get("invokeApiFunction")(context, log, uri, params);
	return "cancel";
}

// Handles the XML generated event.
function onXmlGenerated() {
	context.get("parseResponseFunction")(dom, response);
}

// Handles the transaction start event.
function onTransactionStarted() {
	OAuthService 	= context.get("OAuthService");
	requestToken 	= context.get("requestToken");
	verifier = new Packages.org.scribe.model.Verifier(PinCode);
	accessToken = OAuthService.getAccessToken(requestToken, verifier);
	context.set("accessToken", accessToken);
	return "cancel";
}
