java.lang.String.value:
SELECT t.dis_idt, dis_nam 
FROM twinsoft_reservation_code t, distributor d 
WHERE cam_idt={camping_id} 
AND t.dis_idt=d.dis_idt;

SELECT a.dis_idt, dis_nam, a.pro_idt, pro_nam 
FROM product_availability a, distributor d, product p 
WHERE a.cam_idt={camping_id} 
AND a.dis_idt=d.dis_idt 
AND a.pro_idt=p.pro_idt 
GROUP BY dis_idt, pro_idt;

SELECT a.cam_idt, cam_nam, a.pro_idt, pro_nam 
FROM product_availability a, camping c, product p 
WHERE a.dis_idt={distrib_id} 
AND a.cam_idt=c.cam_idt 
AND a.pro_idt=p.pro_idt 
GROUP BY cam_idt, pro_idt;
